children.defaultOptionValue:
{{ ((options, serverSideFiltering) => ( 
get_voices.data.voices.map((elem) => {
	return {
		"label": elem.name,
		"value": elem.voice_id
	}
})[0].value
))(voice_select.options, voice_select.serverSideFiltering) }}

children.onClick:
{{ 
 (async () => {

let client = storiesHandler.getSupaClient();
		 await client.from("audio_stories").insert({
	id: appsmith.store.currentSelectedStory,
	 chapters: {
		 data: appsmith.store.step_eight_chapters_for_audio[0].chapters.data.map((elem) => {
			 return  {
				 result: elem.result,
				 index: elem.index,
         audioPath: appsmith.store["res_from_upload_" + elem.index].data.path
			 }
		 })
	 }
 });

		 showAlert("chapters audibles saved succesfully", "success");

		 await client.from("stories_contents").update({
	hasAudioGeneration: true
}).eq("id", appsmith.store.currentSelectedStory)

await stories_request.run();
	 closeModal("chapters_gen_modal");

closeModal("audible_generation_modal");


 })()
